ee:message.ee:set-payload:
%dw 2.0
output application/xml
---
{
    hotels: {(payload map (obj, index) -> {
            hotel: {
                booking_id: payload.booking_id,
                persons: payload.persons default 0,
                roomNo: payload.roomNo default 0,
                price: payload.price default 0,
                bookedDate: payload.bookedDate default "",
                hotelDesc: payload.hotelDesc default "",
                location: payload.location default "",
                hotelName: payload.hotelName default ""
            }
        })       
    }
}
